import React from 'react';
import { AppConfig, UserSession } from '@stacks/connect';
import { ConnectProvider }from '@stacks/connect-react';
import ConnectWallet from "./components/ConnectWallet";
import ContractCallVote from "./components/ContractCallVote";

const appConfig = new AppConfig(['store_write', 'publish_data']);
const userSession = new UserSession({ appConfig });


const Reply = ({ reply }) => (
  <div className='reply'>
    <h3>{reply.username}</h3>
    <p>{reply.content}</p>
  </div>
);

// Bubble component
const Bubble = ({ bubble }) => (
  <div className="bubble-container">
    <div className="bubble">

      <h2 style={{ display: 'inline-block', marginLeft: '10px' }}>{bubble.title}</h2>

      <h3> {bubble.username}</h3>
        <p>{bubble.content}</p>

        {bubble.replies.map((reply) => (
          <Reply key={reply.id} reply={reply} />
        ))}
    </div>
  </div>
);

// Thread component 
const Thread = ({ bubbles }) => (
  <div className="thread">
    {bubbles.map((bubble) => (
      <Bubble key={bubble.id} bubble={bubble} />
    ))}
  </div>
);


// Usage
// Array of bubbles
const bubbles = [
  { 
    id: 1, 
    username: 'Marwan',
    title: "Hello World",
    content: 'This is the first bubble.',
    replies: [
      { id: 1, username: 'Shey', content: 'This is a reply.' },
      // more replies...
    ]
  },
  { 
    id: 2, 
    username: 'Shaye',
    title: "I need a gf",
    content: 'Please i need gf immediately',
    replies: [
      { id: 1, username: 'Prath', content: 'I do not volunteer' },
      { id: 1, username: 'Kosta', content: 'Me' },
      // more replies...
    ]
  },

  // more bubbles...
];

function App() {

  return (
    <ConnectProvider userSession={userSession}>

    <div className="App">

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ textAlign: 'center', margin: 0 }}>Bubble</h1>
        <button>New Bubble</button>
      </div>

      <hr />

      <div className='mainBubbles' style={{display: "flex", justifyContent: "center"}}>
            <Thread bubbles={bubbles} />
            {/* ConnectWallet file: `./src/components/ConnectWallet.js` */}
            <ConnectWallet />
            {/* ContractCallVote file: `./src/components/ContractCallVote.js` */}
            <ContractCallVote />
      </div>

    </div>
    </ConnectProvider>
  );
}

export default App;
