import React from 'react';

// Tweet component
const Bubble = ({ bubble }) => (
  <div className="bubble">
    <h2>{bubble.username}</h2>
    <p>{bubble.content}</p>
  </div>
);

// Thread component 
const Thread = ({ bubbles }) => (
  <div className="thread">
    {bubbles.map((bubble) => (
      <Bubble key={bubble.id} bubble={bubble} />
    ))}
  </div>
);

// Usage
// Array of bubbles
const bubbles = [
  { id: 1, username: 'user1', content: 'This is the first bubble.' },
  { id: 2, username: 'user2', content: 'This is the second bubble.' },
  // more tweets...
];

function App() {
  return (
    <div className="App">
      <Thread bubbles={bubbles} />
    </div>
  );
}

export default App;
