import React, { useState } from 'react';



import ConnectWallet from "./components/ConnectWallet";
import ContractCallVote from "./components/ContractCallVote";



const Reply = ({ reply }) => (
  <div className='reply'>
    <h3>{reply.username}</h3>
    <p>{reply.content}</p>
  </div>
);

// Bubble component
const Bubble = ({ bubble }) => (
  <div className="bubble-container">
    <div className="bubble">

      <h2 style={{ display: 'inline-block', marginLeft: '10px' }}>{bubble.title}</h2>

      <h3> {bubble.username}</h3>
        <p>{bubble.content}</p>

        {bubble.replies.map((reply) => (
          <Reply key={reply.id} reply={reply} />
        ))}
    </div>
  </div>
);

// Thread component 
const Thread = ({ bubbles }) => (
  <div className="thread">
    {bubbles.map((bubble) => (
      <Bubble key={bubble.id} bubble={bubble} />
    ))}
  </div>
);

const NewBubble = ({ onNewBubble }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    onNewBubble({ title, content });
    setTitle('');
    setContent('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      <textarea placeholder="Content" value={content} onChange={(e) => setContent(e.target.value)} />
      <button type="submit">Create Bubble</button>
    </form>
  );
};


function Welcome({ onConnect }) {
  return (
    <div className="welcome">
      <h1>Welcome to Bubble</h1>
      <p>Please connect your wallet to continue.</p>
      <button onClick={onConnect}>Connect Wallet</button>
    </div>
  );
}

// Usage
// Array of bubbles
const bubbles = [
  { 
    id: 1, 
    username: 'Marwan',
    title: "Hello World",
    content: 'This is the first bubble.',
    replies: [
      { id: 1, username: 'Shey', content: 'This is a reply.' },
      // more replies...
    ]
  },
  { 
    id: 2, 
    username: 'Shaye',
    title: "I need a gf",
    content: 'Please i need gf immediately',
    replies: [
      { id: 1, username: 'Prath', content: 'I do not volunteer' },
      { id: 1, username: 'Kosta', content: 'Me' },
      // more replies...
    ]
  },

  // more bubbles...
];
function App() {
  const [bubbles, setBubbles] = useState([]);
  const [isConnected, setIsConnected] = useState(false);

  const handleNewBubble = (bubble) => {
    setBubbles([...bubbles, bubble]);
  };

  const handleConnect = () => {
    setIsConnected(true);
  };

  return (
    <ConnectProvider userSession={userSession}>
      <div className="App">
        {isConnected ? (
          <>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <h1 style={{ textAlign: 'center', margin: 0 }}>Bubble</h1>
              <button>New Bubble</button>
            </div>
            <hr />
            <div className="mainBubbles" style={{ display: 'flex', justifyContent: 'center' }}>
              <Thread bubbles={bubbles} />
            </div>
            <ConnectWallet />
            <ContractCallVote />
          </>
        ) : (
          <Welcome onConnect={handleConnect} />
        )}
      </div>
    </ConnectProvider>
  );
}

export default App;
