import React, { useState } from 'react';
import ConnectWallet from "./components/ConnectWallet";
import ContractCallVote from "./components/ContractCallVote";


const Reply = ({ reply }) => (
  <div className='reply'>
    <h3>{reply.username}</h3>
    <p>{reply.content}</p>
  </div>
);

// Bubble component
const Bubble = ({ bubble }) => (
  <div className="bubble-container">
    <div className="bubble">
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2 style={{ display: 'inline-block'}}>{bubble.title}</h2>
        <button>Reply</button>
    </div>


      <div>
        <h3> {bubble.username}</h3>
        <p>{bubble.content}</p>
        <hr></hr>
      </div>

        {bubble.replies.map((reply) => (
          <div>
          <Reply key={reply.id} reply={reply} />
          <hr></hr>
          </div>

        ))}
    </div>
  </div>
);

// Thread component 
const Thread = ({ bubbles }) => (
  <div className="thread">
    {bubbles.map((bubble) => (
      <Bubble key={bubble.id} bubble={bubble} />
    ))}
  </div>
);





const NewBubble = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    setTitle('');
    setContent('');
    <ContractCallVote/>
  };

  return (
    <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: "#123444", padding: '20px', borderRadius: '10px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)' }}>
      <form onSubmit={handleSubmit}>
        <textarea type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} style={{ display: 'block', width: '100%', padding: '10px', marginBottom: '10px', border: 'none', borderBottom: '1px solid #ccc', fontSize: '18px', color:"grey", height: '50px', width: "400px" }} />
        <textarea placeholder="Content" value={content} onChange={(e) => setContent(e.target.value)} style={{ display: 'block', width: '100%', padding: '10px', marginBottom: '10px', border: 'none', borderBottom: '1px solid #ccc', fontSize: '18px', color:"grey", height: '200px', width: "400px" }} />
        <button type="submit" style={{ display: 'block', width: '100%', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', fontSize: '18px', cursor: 'pointer' }}>Create Bubble</button>
        <button type="button" onClick={NewBubble} style={{ display: 'block', width: '100%', padding: '10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', fontSize: '18px', cursor: 'pointer', marginTop: '10px' }}>Cancel</button>
      </form>
    </div>
  );
};



// Usage
// Array of bubbles
const bubbles = [
  { 
    id: 1, 
    username: 'Marwan',
    title: "Hello World",
    content: 'This is the first bubble.',
    replies: [
      { id: 1, username: 'Shey', content: 'This is a reply.' },
      // more replies...
    ]
  },
  { 
    id: 2, 
    username: 'Shaye',
    title: "I need a gf",
    content: 'Please i need gf immediately',
    replies: [
      { id: 1, username: 'Prath', content: 'I do not volunteer' },
      { id: 1, username: 'Costa', content: 'Me' },
      // more replies...
    ]
  },
  { 
    id: 2, 
    username: 'Shaye',
    title: "I need a gf",
    content: 'Please i need gf immediately',
    replies: [
      { id: 1, username: 'Prath', content: 'I do not volunteer' },
      { id: 1, username: 'Costa', content: 'Me' },
      // more replies...
    ]
  },
  { 
    id: 2, 
    username: 'Shaye',
    title: "I need a gf",
    content: 'Please i need gf immediately',
    replies: [
      { id: 1, username: 'Prath', content: 'I do not volunteer' },
      { id: 1, username: 'Costa', content: 'Me' },
      // more replies...
    ]
  },

  // more bubbles...
];
function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [showNewBubble, setShowNewBubble] = useState(false);

  const handleConnect = () => {
    setIsConnected(true);
  };

  const handleNewBubble = () => {
    setShowNewBubble(!showNewBubble);
  };

  return (
    <div className="App">
      {isConnected ? (
        <>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <h1 style={{ textAlign: 'center', margin: 0 }}>Bubble</h1>
            <ConnectWallet onConnect={handleConnect} />
            <button onClick={handleNewBubble}>New Bubble.</button>
          </div>
          <hr />
          <div className="mainBubbles" style={{ display: 'flex', justifyContent: 'center' }}>
            <Thread bubbles={bubbles} />
          </div>
          {showNewBubble && <NewBubble onNewBubble={() => {}} />}
        </>
      ) : (
        <div className="welcome-text" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', height: '100vh' }}>
          <h1>Welcome to Bubble</h1>
          <p>Please connect your wallet to continue.</p>
          <ConnectWallet onConnect={handleConnect} />
        </div>
      )}
    </div>
  );
}

export default App;
